import com.eviware.soapui.support.XmlHolder
import org.apache.poi.hssf.usermodel.HSSFCell;
import org.apache.poi.ss.usermodel.Cell;
import org.apache.poi.ss.usermodel.CellStyle;
import org.apache.poi.ss.usermodel.DataFormat;
import org.apache.poi.ss.usermodel.Row;
import org.apache.poi.ss.usermodel.Sheet;
import org.apache.poi.ss.usermodel.Workbook;
import org.apache.poi.xssf.usermodel.XSSFCell;
import org.apache.poi.xssf.usermodel.XSSFRow;
import org.apache.poi.xssf.usermodel.XSSFSheet;
import org.apache.poi.xssf.usermodel.XSSFWorkbook;
    try {
        // Variable Declarations 
        def myTestCase = context.testCase; //myTestCase contains the test case 
        def counter, next, previous, size; //Variables used to handle the loop and to move inside the file        
        XSSFWorkbook workbook1 = new XSSFWorkbook(new FileInputStream("C:/Users/Administrator/Desktop/Utility/XMLComparison/FileComparison/FileNames.xlsx")); //XL file containing the data  
        XSSFSheet sheet1 = workbook1.getSheetAt(0) //getSheet(0) represents Sheet1 in XL file 
        size = sheet1.getPhysicalNumberOfRows()//get the number of rows, each row is a data set
        propTestStep = myTestCase.getTestStepByName("Properties") // get the Property TestStep object 
        propTestStep.setPropertyValue("Total", size.toString())
        counter = propTestStep.getPropertyValue("Count").toString() //counter variable contains iteration number
        counter = counter.toInteger()
        next = (counter > size - 2 ? 0 : counter + 1)
        // Reading data from file
        XSSFCell TestCaseID = sheet1.getRow(counter).getCell(0) // getCell(column,row). getCell(0,0) represents A1
        XSSFCell ExpectedFile = sheet1.getRow(counter).getCell(1)
        XSSFCell ActualFile = sheet1.getRow(counter).getCell(2)


        String eFilePath = ExpectedFile.getStringCellValue()  
        String eFileContents = new File(eFilePath).getText('UTF-8')
        String aFilePath = ActualFile.getStringCellValue()  
        String aFileContents = new File(aFilePath).getText('UTF-8')

        propTestStep.setPropertyValue("TestCaseID", TestCaseID.toString()) //the value is saved in the property 
        propTestStep.setPropertyValue("ExpectedFile", eFileContents.toString()) //the value is saved in the property
        propTestStep.setPropertyValue("ActualFile", aFileContents.toString()) //the value is saved in the property
        propTestStep.setPropertyValue("Count", next.toString()) //increase Count value 
        next++ //increase next value
        propTestStep.setPropertyValue("Next", next.toString()) //set Next value on the properties step 
        //Decide if the test has to be run again or not
        if (counter == size - 1) {
            propTestStep.setPropertyValue("StopLoop", "T")
            propTestStep.setPropertyValue("Count", "1")
            log.info "Setting the stoploop property now..."
        } else if (counter == 0) {
            def runner = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testRunner.testCase, null)
            propTestStep.setPropertyValue("StopLoop", "F")
            assert true
        } else {
            propTestStep.setPropertyValue("StopLoop", "F")
        }
        assert true;
    }
catch (exc) {
    log.error("Exception happened: " + exc.toString());
}

import com.eviware.soapui.support.*;
import org.custommonkey.xmlunit.*;
import org.xml.sax.SAXException;
import groovy.io.FileType;
import groovy.xml.XmlUtil;
import jxl.*;

//Get date
def date = new Date();
def dts = date.format("yyyy-MM-dd-HH-mm-ss-ms");

//Create report file
def reportFile = "C:/Users/Administrator/Desktop/Utility/XMLComparison/FileComparison/" + "FC_ExecutionReport" + ".csv";
def report = new File(reportFile);
if (!report.exists()) {
 report.createNewFile();
 report.write('"Test Case ID","Total Differences","Result Message","Execution Date"');
}

//Retrieve Test case ID
def myTestCase = context.testCase;
def propTestStep = myTestCase.getTestStepByName("Properties");
def TestCaseID = propTestStep.getPropertyValue("TestCaseID").toString();

//Get documents for comparison
def expFile = context.expand('${Properties#ExpectedFile}');
def actFile = context.expand('${Properties#ActualFile}');

// Creates a list of elements to ignore
Set < String > ignoreList = new HashSet < String > ();
ignoreList.add("Date")
ignoreList.add("Id")

//variable declaration
List allDifferences;
int diffSize = 0;
def msgBuffer = new StringBuffer();

//checking file size
if (expFile == '' || actFile == '') {
 msgBuffer.append("Loaded empty file")
} else {

 //configuring XMLUnit
 XMLUnit.setIgnoreWhitespace(true);
 XMLUnit.setIgnoreComments(true)
 XMLUnit.setIgnoreDiffBetweenTextAndCDATA(true)
 XMLUnit.setNormalizeWhitespace(true)
 XMLUnit.setIgnoreAttributeOrder(true);
 XMLUnit.setIgnoreDiffBetweenTextAndCDATA(true);

 // Create an object with differences between documents
 Diff myDiff = new Diff(expFile, actFile)
 DetailedDiff diff = new DetailedDiff(myDiff);

 // Get list of all differences
 allDifferences = diff.getAllDifferences();
 diffSize = allDifferences.size();

 if (allDifferences.size() == 0) {
  msgBuffer.append("Files are Identical")
 } else {
  int j = 0;
  for (int i = 0; i < allDifferences.size(); i++) {
   diffNodeName = "";
   try {
    j++;
    diffNodeName = allDifferences.get(i).getControlNodeDetail().getNode().getParentNode().getNodeName()
    if (!ignoreList.contains(diffNodeName)) {
     msgBuffer.append(j + "." + allDifferences[i])
     msgBuffer.append('\n')
    } else {
     msgBuffer.append(j + "." + "Difference in ignore list tag -" + diffNodeName + '\n')
    }
   } catch (exc) {
    msgBuffer.append(j + "." + allDifferences[i])
    msgBuffer.append('\n')
   }
  }
 }
}

//inserting data into report file
report.append('\n');
report.append('"' + TestCaseID + '",');
report.append('"' + diffSize + '",');
report.append('"' + msgBuffer + '",');
report.append('"' + dts + '",');

def myTestCase = context.testCase 
def runner 
propTestStep = myTestCase.getTestStepByName("Properties") // get the Property TestStep
endLoop = propTestStep.getPropertyValue("StopLoop").toString() 
if (endLoop.toString() == "T" || endLoop.toString()=="True" || endLoop.toString()=="true") 
{ 
log.info ("Exit Groovy Data Source Looper") 
assert true 
} 
else 
{ 
testRunner.gotoStepByName("ReadFiles") //setStartStep 
}

