import com.eviware.soapui.support.*
import org.custommonkey.xmlunit.*
import org.xml.sax.SAXException
import groovy.io.FileType
import groovy.xml.XmlUtil
import jxl.*

// Get date
def date = new Date()
def dts = date.format("yyyy-MM-dd-HH-mm-ss-ms")

// Create report file
def reportFile = "C:/SoapUI/New/" + "RC_ExecutionReport" + ".csv"
def report = new File(reportFile)
if (!report.exists()) {
    report.createNewFile()
    report.write('"Test Case ID","Total Differences","Result Message","Execution Date"')
}

// Retrieve Test case ID
def myTestCase = context.testCase
def propTestStep = myTestCase.getTestStepByName("Properties")
def TestCaseID = propTestStep.getPropertyValue("TestCaseID").toString()

// Get documents for comparison
def expFile = context.expand('${OldVersion#Response}')
def actFile = context.expand('${NewVersion#Response}')

// Creates a list of elements to ignore
Set<String> ignoreList = new HashSet<String>()
ignoreList.add("m:sCountryFlag")

// Variable declaration
List allDifferences
int diffSize = 0
def msgBuffer = new StringBuffer()

// Checking file size
if (expFile == '' || actFile == '') {
    msgBuffer.append("Loaded empty file")
} else {

    // Configuring XMLUnit
    XMLUnit.setIgnoreWhitespace(true)
    XMLUnit.setIgnoreComments(true)
    XMLUnit.setIgnoreDiffBetweenTextAndCDATA(true)
    XMLUnit.setNormalizeWhitespace(true)
    XMLUnit.setIgnoreAttributeOrder(true)

    // Create an object with differences between documents
    Diff myDiff = new Diff(expFile, actFile)
    DetailedDiff diff = new DetailedDiff(myDiff)

    // Get list of all differences
    allDifferences = diff.getAllDifferences()
    diffSize = allDifferences.size()

    if (allDifferences.size() == 0) {
        msgBuffer.append("Files are Identical")
    } else {
        int j = 0
        for (int i = 0; i < allDifferences.size(); i++) {
            diffNodeName = ""
            try {
                j++
                // Get expected and actual values
                def expected = allDifferences.get(i).getControlNodeDetail().getValue()
                def actual = allDifferences.get(i).getTestNodeDetail().getValue()

                // Get XPath for the difference
                def xpath = allDifferences.get(i).getControlNodeDetail().getXpathLocation()

                // Build the custom message format: Expected - 'value' | Actual - 'value' | XPath
                if (!ignoreList.contains(diffNodeName)) {
                    msgBuffer.append(j + ". Expected - '" + expected + "' | Actual - '" + actual + "' | XPath: " + xpath + '\n')
                } else {
                    msgBuffer.append(j + ". Difference in ignore list tag - " + diffNodeName + '\n')
                }
            } catch (exc) {
                msgBuffer.append(j + ". " + allDifferences[i] + '\n')
            }
        }
    }
}

// Inserting data into report file
report.append('\n')
report.append('"' + TestCaseID + '",')
report.append('"' + diffSize + '",')
report.append('"' + msgBuffer.toString().replace('"', '""') + '",')
report.append('"' + dts + '",')

// Create request and response files
def myXmlRequestOld = "C:/SoapUI/New/TestResults/" + TestCaseID + "_OldVersion_Request" + ".xml"
def requestOld = context.expand('${OldVersion#Request}')
def reqOld = new File(myXmlRequestOld)
reqOld.write(requestOld, "UTF-8")

def myXmlResponseOld = "C:/SoapUI/New/TestResults/" + TestCaseID + "_OldVersion_Response" + ".xml"
def responseOld = context.expand('${OldVersion#Response}')
def respOld = new File(myXmlResponseOld)
respOld.write(responseOld, "UTF-8")

def myXmlRequestNew = "C:/SoapUI/New/TestResults/" + TestCaseID + "_NewVersion_Request" + ".xml"
def requestNew = context.expand('${NewVersion#Request}')
def reqNew = new File(myXmlRequestNew)
reqNew.write(requestNew, "UTF-8")

def myXmlResponseNew = "C:/SoapUI/New/TestResults/" + TestCaseID + "_NewVersion_Response" + ".xml"
def responseNew = context.expand('${NewVersion#Response}')
def respNew = new File(myXmlResponseNew)
respNew.write(responseNew, "UTF-8")

def myTestCase = context.testCase 
def runner 
propTestStep = myTestCase.getTestStepByName("Properties") // get the Property TestStep
endLoop = propTestStep.getPropertyValue("StopLoop").toString() 
if (endLoop.toString() == "T" || endLoop.toString()=="True" || endLoop.toString()=="true") 
{ 
log.info ("Exit Groovy Data Source Looper") 
assert true 
} 
else 
{ 
testRunner.gotoStepByName("ReadFiles") //setStartStep 
}
